import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function QuizList() {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    setLoading(true);
    setError("");

    fetch("http://localhost:5000/quiz/list")
      .then(async (res) => {
        const data = await res.json().catch(() => []);
        if (!res.ok) throw new Error(data.message || `Server error (${res.status})`);
        return data;
      })
      .then((data) => setQuizzes(Array.isArray(data) ? data : []))
      .catch((err) => setError(err.message || "Failed to load quizzes"))
      .finally(() => setLoading(false));
  }, []);

  async function deleteQuiz(id) {
    const ok = window.confirm("Are you sure you want to delete this quiz?");
    if (!ok) return;

    const res = await fetch(`http://localhost:5000/quiz/${id}`, { method: "DELETE" });
    const data = await res.json().catch(() => ({}));

    if (!res.ok) return alert(data.message || "Failed to delete quiz.");

    setQuizzes((prev) => prev.filter((q) => q._id !== id));
  }

  if (loading) return <div className="container">Loading quizzesâ€¦</div>;
  if (error) return <div className="container" style={{ color: "red" }}>Error: {error}</div>;

  return (
    <div className="container">
      <h2>Available Quizzes</h2>

      {quizzes.length === 0 ? (
        <p>No quizzes found.</p>
      ) : (
        <ul>
          {quizzes.map((q) => (
            <li key={q._id}>
              <strong>{q.title || "Untitled Quiz"}</strong>
              <br />

              <Link to={`/quiz/${q._id}`}>
                <button>Take Quiz</button>
              </Link>

              <Link to={`/quiz/${q._id}/add-question`}>
                <button>Add Question</button>
              </Link>

              <button className="delete-btn" onClick={() => deleteQuiz(q._id)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}

      <hr />

      <Link to="/create">
        <button>Add New Quiz</button>
      </Link>

      <Link to="/history">
        <button>View My Results</button>
      </Link>
    </div>
  );
}

export default QuizList;
